CREATE VIEW `v_pagos_pendientes` AS select `cli`.`id_cliente` AS `id_cliente`,concat(`cli`.`nombres`,' ',`cli`.`apellidos`) AS `cliente`,`vm`.`concepto` AS `concepto`,`vm`.`total` AS `total`,`vm`.`fecha_limite` AS `fecha_limite`,dayofmonth(`vm`.`fecha_limite`) AS `dia`,monthname(`vm`.`fecha_limite`) AS `mes` from ((`v_morosos` `vm` join `ic_contratos` `c` on((`c`.`id_contrato` = `vm`.`id_contrato`))) join `ic_clientes` `cli` on((`cli`.`id_cliente` = `c`.`id_cliente`))) where (`vm`.`estado` = 'no pagado') order by `vm`.`fecha_limite`;
